dbscript.install:
DROP TABLE IF EXISTS `yc_guest`;
CREATE TABLE `yc_guest` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `name` varchar(50) DEFAULT NULL COMMENT '用户名',
  `email` varchar(50) DEFAULT NULL COMMENT '邮件',
  `title` varchar(100) DEFAULT NULL COMMENT '标题',
  `content` text COMMENT '内容',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='留言表';
INSERT INTO `yc_auth_rule` (`icon`, `title`, `href`, `type`, `status`, `condition`, `pid`, `sort`, `create_time`, `update_time`) VALUES('',	'留言列表',	'guest/guest/guestlist',	1,	1,	NULL,	0,	0,	NULL,	NULL);
SET SQL_SAFE_UPDATES =0;
update yc_auth_rule set pid =(select id from (select id from yc_auth_rule WHERE href = 'admin')as t) WHERE href like 'guest/%';
SET SQL_SAFE_UPDATES =1;
